filters.with_process:
function(doc, req){

  if(!doc.process)
    return false;

  if (req.query.last) {

    if(doc.process.length == 0)
      return req.query.last === '0';

    return doc.process[ doc.process.length-1 ].name === req.query.last;
  }

  //could add other query parameters, such as 'first' or size to match against the number of values in the doc.process array

  return false;
}

lastproc.map:
function (doc) {
    if (doc.status === 'good' && doc.conditions && doc.process) {
    	if(doc.process.length == 0)
        	emit([0, doc._id], doc.conditions);
        else if (doc.process[ doc.process.length-1 ].name) //emit the last process completed
        	emit( [doc.process[ doc.process.length-1 ].name, doc._id], doc.conditions);
    }
}
